mapper.select:
		SELECT heal_id, heal_name, heal_amount, heal_done, heal_date
		FROM health


            	mem_id = #{memId}


	            AND heal_name LIKE #{healName}


	            AND heal_done = #{healDone}


	        	AND heal_date = #{healDate}

SELECT heal_id, heal_name, heal_amount, heal_done, heal_date, mem_id
FROM health
WHERE heal_id=#{VALUE}

mapper.update:
UPDATE health
SET heal_name=#{healName}, heal_amount=#{healAmount}, heal_done=#{healDone}
WHERE heal_id=#{healId}
