mapper.select:
SELECT mem_id, email, password, nickname, gender, profile_img,
goal_lv, goal_name, goal_exp
FROM member
WHERE email=#{email}

    AND password=#{password}


SELECT mem_id, email, password, nickname, gender, profile_img
FROM member
WHERE nickname=#{VALUE}


SELECT mem_id, email, password, nickname, gender, profile_img, goal_lv, goal_name, goal_exp
FROM member
WHERE mem_id=#{VALUE}

mapper.update:
UPDATE member
SET email=#{email}, password=#{password}, nickname=#{nickname}, gender=#{gender}, profile_img=#{profileImg}
WHERE mem_id=#{memId}

mapper.select:
